    sub (GET + /nearest/**.*) {
        my ($self, $point) = @_;
        foreach (@PLACES) {
            if ($point eq "4326/$_->[2],$_->[1]") {
                return $self->output({
                    postcode => { wgs84_lat => $_->[1], wgs84_lon => $_->[2], postcode => $_->[0], distance => 93 },
                });
            }
        }
        return $self->output({});
    },

